#!/bin/perl -w

use Bio::Seq;
use Bio::SeqIO;

$seq_obj = Bio::Seq->new(-seq => "aaaatgggggggggggccccgtt",
                         -display_id => "#12345",
                         -desc => "example 1" );

$seq_io = Bio::SeqIO->new(-file => '>sequence.fasta', 
                          -format => 'fasta' );

$seq_io->write_seq($seq_obj);

my $seq_in  = Bio::SeqIO->new( -format => 'fasta',
                               -file   => $string);

while( my $seq = $seq_in->next_seq() ) {
  my $factory = Bio::Tools::Run::StandAloneBlast->new(
		  outfile => $seq->display_id . ".blastn",
		  program => 'blastn' );
  my $bl2seq_report = $factory->bl2seq( 'ketosintaza-dna', $seq);
}
